<?php

namespace Kali\Back\ProductBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * ProductRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProductRepository extends EntityRepository {

    /**
     * 
     * @description Retourne la liste des 10 produits les plus vendus
     * @return array(Product)
     */
    public function findPopular() {
        return $this->getEntityManager()
                        ->createQuery(
                                'SELECT p FROM KaliBackProductBundle:Product p'
                        )
                        ->setMaxResults(10)
                        ->getResult();
    }

    /**
     * 
     * @description Retourne la liste des  produits par catÃ©gorie
     * @return array(Product)
     */
    public function findByCat(Category $category) {
        return $this->_em->createQuery('SELECT p FROM KaliBackProductBundle:Product p JOIN p.categories c WHERE c=:category')
                        ->setParameter('category', $category)
                        ->getResult();
    }
}
